mapper.select:
SELECT SEQ, TITLE, WRITER, CONTENT, REGDATE, CNT
FROM BOARD
WHERE SEQ = #{seq}

mapper.insert:
    	INSERT INTO BOARD
    	(
   		  	  SEQ
			, TITLE
			, WRITER
			, CONTENT
			, REGDATE
			, CNT
    	)
    	VALUES
    	(
    		  #{seq}
			, #{title}
			, #{writer}
			, #{content}
			, now()
			, 0
    	)
