mapper.insert:
INSERT INTO doctor(
	  doctor_email
	, doctor_pwd
	, doctor_name
	, doctor_birth
	, doctor_sex
	, doctor_addr_main
	, doctor_addr_detail
	, file_key			
)
VALUES(
	  #{doctorEmail}
	, #{doctorPwd}
	, #{doctorName}
	, #{doctorBirth}
	, #{doctorSex}
	, #{doctorAddrMain}
	, #{doctorAddrDetail}
	, #{fileKey}
)

mapper.update:
UPDATE doctor
SET doctor_name = #{doctorName}, doctor_subject = #{doctorSubject}, doctor_major=#{doctorMajor}, doctor_subject=#{doctorSubject}, hospital_id = (SELECT hospital_id FROM hospital WHERE hospital_name = #{hospitalName}),  file_key = COALESCE(#{fileKey}, file_key)
WHERE doctor_id = #{doctorId}

UPDATE doctor d SET d.doctor_confirm_yn = 'Y'
WHERE d.doctor_confirm_yn = 'N' 
AND d.doctor_email = #{doctorEmail}

mapper.select:
SELECT sum(p.amount) AS sum FROM payments p
WHERE p.doctor_id = #{doctorId}
AND p.payment_status = 'N'

SELECT doctor_email 
FROM doctor
WHERE doctor_id = #{doctorId}
