mapper.insert:
INSERT INTO inquiry
(
	  inquiry_type
	, inquiry_title
	, inquiry_writer_email
	, inquiry_comments
	, file_key
)
VALUES
(
	  #{inquiryType}
	, #{inquiryTitle}
	, #{inquiryWriterEmail}
	, #{inquiryComments}
	, #{fileKey}
)

mapper.update:
UPDATE inquiry
   SET inquiry_answer 		  = #{inquiryAnswer}
     , inquiry_answer_regdate = now()
     , status 				  = 'Y'
 WHERE inquiry_id 			  = #{inquiryId}

mapper.select:
SELECT i.inquiry_id AS inquiry_id , i.inquiry_type AS inquiry_type , i.inquiry_title AS inquiry_title , i.inquiry_writer_email AS inquiry_writer_email , i.inquiry_comments AS inquiry_comments , TO_CHAR(i.inquiry_regdate, 'YYYY-mm-DD') AS inquiry_regdate , i.inquiry_answer AS inquiry_answer , TO_CHAR(i.inquiry_answer_regdate, 'YYYY-mm-DD') AS inquiry_answer_regdate , i.file_key AS file_key , i.status AS status , d.drugstore_name AS drugstore_name FROM inquiry i INNER JOIN drugstore d ON i.inquiry_writer_email = d.drugstore_email
WHERE d.drugstore_id = #{drugstoreId}
ORDER BY CASE WHEN i.status = 'N' THEN 1 ELSE 2 END , i.inquiry_regdate desc

SELECT i.inquiry_id AS inquiry_id , i.inquiry_type AS inquiry_type , i.inquiry_title AS inquiry_title , i.inquiry_writer_email AS inquiry_writer_email , i.inquiry_comments AS inquiry_comments , TO_CHAR(i.inquiry_regdate, 'YYYY-mm-DD') AS inquiry_regdate , i.inquiry_answer AS inquiry_answer , TO_CHAR(i.inquiry_answer_regdate, 'YYYY-mm-DD') AS inquiry_answer_regdate , i.file_key AS file_key , i.status AS status FROM inquiry i INNER JOIN doctor d ON i.inquiry_writer_email = d.doctor_email
WHERE d.doctor_id = #{doctorId}
ORDER BY CASE WHEN i.status = 'N' THEN 1 ELSE 2 END , i.inquiry_regdate desc
